import React, { useEffect } from 'react'
import {useLocation,Link} from 'react-router-dom'
import './Yourorders.css'
import { useContext } from 'react'
import { ShopContext } from '../../Context/ShopContext'

const Yourorders = async () => {

  const {all_product} = useContext(ShopContext)


  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const items = params.get('items');
  if(items){
    if(localStorage.getItem('auth-token')){
      fetch("http://localhost:4000/addtocart",{
          method:'POST',
          headers:{
              Accept:'application/form-data',
              'auth-token':`${localStorage.getItem('auth-token')}`,
              'Content-Type':'application/json',
          },
          body:JSON.stringify({"itemId":itemId})
      })
      .then((resp)=>resp.json())
      .then((data)=>console.log(data))
  }
  }
  const cartItems = items ? JSON.parse(decodeURIComponent(items)) : [];

  const findImage = (id)=>{
    const product = all_product.find((e)=> e.id === Number(id))
    return product? product.image:' '
  }

  return (
    <div className='your-orders'>
      <h1 className='main'>Your Orders</h1>
      <hr/>
      <div className='order-list'>
      {
        cartItems.map((item, index) => (
           <div className='top'>
            <div key={index} className='order-item'>
            <div className='description'>
              <div className='name'>
                <h1>{item.name}</h1> 
                <div>{item.quantity}</div>
              </div>
              <div className='btn'>
                <button id='review'>ADD REVIEW</button>
                <Link to={`/product/${item.id}`}><button id='viewproduct'>VIEW PRODUCT</button></Link>
              </div>
            </div>
            <img  src={findImage(item.id)} alt=''/>
           </div>
           <hr/>
           </div>
        ))
      }
      </div>
    </div>
  )
}

export default Yourorders
